import React from 'react';
import BookTerm from '../BooksTerm/bookTerm';
import {Link} from 'react-router-dom';
import ReactPaginate from 'react-paginate';

class Books extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            page: 0,
            size: 5
        }
    }

    render() {
        const offset = this.state.size * this.state.page;
        const nextPageOffset = offset + this.state.size;
        const pageCount = Math.ceil(this.props.books.length / this.state.size);
        const books = this.getBooksPage(offset, nextPageOffset);

        return (
            <div className={"container"}>
                <hr/>
                <h1 className={"text-left"}>Books</h1>
                <hr/>
                <div>
                    <table className={"table"}>
                        <thead>
                        <tr>
                            <th scope={"col"}>Name</th>
                            <th scope={"col"}>Category</th>
                            <th scope={"col"}>Author</th>
                            <th scope={"col"}>Available Copies</th>
                            <th scope={"col"}></th>
                        </tr>
                        </thead>
                        <tbody>
                        {books}
                        </tbody>
                    </table>
                </div>

                <div className="col mb-3">
                    <div className="row">
                        <div className="col-12">
                            <Link className={"btn btn-block btn-primary"} to={"/books/add"}>Add book</Link>
                        </div>
                    </div>
                </div>

                <ReactPaginate previousLabel={"back"}
                               nextLabel={"next"}
                               breakLabel={<a href="/#">...</a>}
                               breakClassName={"break-me"}
                               pageClassName={"ml-1"}
                               pageCount={pageCount}
                               marginPagesDisplayed={2}
                               pageRangeDisplayed={5}
                               onPageChange={this.handlePageClick}
                               containerClassName={"pagination m-4 justify-content-center"}
                               activeClassName={"active"}/>
            </div>
        )
    }

    handlePageClick = (data) => {
        let selected = data.selected;
        this.setState({
            page: selected
        })
    }
    getBooksPage = (offset, nextPageOffset) => {
        return this.props.books.map((term) => {
            return (
                <BookTerm key={term.id} term={term} onDelete={this.props.onDelete} onEdit={this.props.onEdit}
                          onGetCopy={this.props.onGetCopy} onAddCopy={this.props.onAddCopy}/>
            );
        }).filter((book, index) => {
            return index >= offset && index < nextPageOffset;
        })
    }
}

export default Books;
